import React, { useCallback, useState } from 'react';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import Image from '@tiptap/extension-image';
import Link from '@tiptap/extension-link';
import TextAlign from '@tiptap/extension-text-align';
import Underline from '@tiptap/extension-underline';
import Highlight from '@tiptap/extension-highlight';
import CharacterCount from '@tiptap/extension-character-count';
import { toast } from 'react-hot-toast';
import axios from 'axios';

const ArticleEditor = ({ 
  initialContent = '', 
  initialTitle = '', 
  onSave, 
  onPublish, 
  articleId = null,
  isLoading = false 
}) => {
  const [title, setTitle] = useState(initialTitle);
  const [isSaving, setIsSaving] = useState(false);

  const editor = useEditor({
    extensions: [
      StarterKit,
      Image.configure({
        inline: true,
        HTMLAttributes: {
          class: 'rounded-lg max-w-full h-auto',
        },
      }),
      Link.configure({
        openOnClick: false,
        HTMLAttributes: {
          class: 'text-blue-600 hover:text-blue-800 underline',
        },
      }),
      TextAlign.configure({
        types: ['heading', 'paragraph'],
      }),
      Underline,
      Highlight.configure({
        multicolor: true,
      }),
      CharacterCount.configure({
        limit: 10000,
      }),
    ],
    content: initialContent,
    editorProps: {
      attributes: {
        class: 'prose prose-lg max-w-none focus:outline-none min-h-[400px] p-4 border rounded-lg',
      },
    },
  });

  const addImage = useCallback(() => {
    const url = window.prompt('Enter image URL:');
    if (url) {
      editor.chain().focus().setImage({ src: url }).run();
    }
  }, [editor]);

  const addLink = useCallback(() => {
    const previousUrl = editor.getAttributes('link').href;
    const url = window.prompt('Enter URL:', previousUrl);

    if (url === null) {
      return;
    }

    if (url === '') {
      editor.chain().focus().extendMarkRange('link').unsetLink().run();
      return;
    }

    editor.chain().focus().extendMarkRange('link').setLink({ href: url }).run();
  }, [editor]);

  const handleSaveDraft = async () => {
    if (!title.trim()) {
      toast.error('Please enter a title');
      return;
    }

    setIsSaving(true);
    try {
      const content = editor.getHTML();
      await onSave({
        id: articleId,
        title: title.trim(),
        content,
        status: 'draft'
      });
      toast.success('Draft saved successfully!');
    } catch (error) {
      toast.error('Failed to save draft');
      console.error('Save error:', error);
    } finally {
      setIsSaving(false);
    }
  };

  const handlePublish = async () => {
    if (!title.trim()) {
      toast.error('Please enter a title');
      return;
    }

    if (!editor.getText().trim()) {
      toast.error('Please add some content');
      return;
    }

    setIsSaving(true);
    try {
      const content = editor.getHTML();
      await onPublish({
        id: articleId,
        title: title.trim(),
        content,
        status: 'published'
      });
      toast.success('Article published successfully!');
    } catch (error) {
      toast.error('Failed to publish article');
      console.error('Publish error:', error);
    } finally {
      setIsSaving(false);
    }
  };

  if (!editor) {
    return <div className="text-center py-8">Loading editor...</div>;
  }

  const characterCount = editor.storage.characterCount.characters();
  const wordCount = editor.storage.characterCount.words();

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white">
      {/* Title Input */}
      <div className="mb-6">
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter article title..."
          className="w-full text-3xl font-bold border-none outline-none placeholder-gray-400 focus:ring-0"
          disabled={isLoading}
        />
      </div>

      {/* Editor Toolbar */}
      <div className="border-b pb-4 mb-4">
        <div className="flex flex-wrap gap-2">
          {/* Text Formatting */}
          <button
            onClick={() => editor.chain().focus().toggleBold().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('bold') ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            <strong>B</strong>
          </button>
          
          <button
            onClick={() => editor.chain().focus().toggleItalic().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('italic') ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            <em>I</em>
          </button>

          <button
            onClick={() => editor.chain().focus().toggleUnderline().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('underline') ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            <u>U</u>
          </button>

          <button
            onClick={() => editor.chain().focus().toggleHighlight().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('highlight') ? 'bg-yellow-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            ⚡
          </button>

          {/* Headings */}
          <button
            onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('heading', { level: 1 }) ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            H1
          </button>

          <button
            onClick={() => editor.chain().focus().toggleHeading({ level: 2 }).run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('heading', { level: 2 }) ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            H2
          </button>

          {/* Lists */}
          <button
            onClick={() => editor.chain().focus().toggleBulletList().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('bulletList') ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            • List
          </button>

          <button
            onClick={() => editor.chain().focus().toggleOrderedList().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('orderedList') ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            1. List
          </button>

          {/* Alignment */}
          <button
            onClick={() => editor.chain().focus().setTextAlign('left').run()}
            className={`px-3 py-1 rounded ${
              editor.isActive({ textAlign: 'left' }) ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            ⬅️
          </button>

          <button
            onClick={() => editor.chain().focus().setTextAlign('center').run()}
            className={`px-3 py-1 rounded ${
              editor.isActive({ textAlign: 'center' }) ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            ↔️
          </button>

          <button
            onClick={() => editor.chain().focus().setTextAlign('right').run()}
            className={`px-3 py-1 rounded ${
              editor.isActive({ textAlign: 'right' }) ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            ➡️
          </button>

          {/* Media */}
          <button
            onClick={addImage}
            className="px-3 py-1 rounded hover:bg-gray-100"
            type="button"
          >
            🖼️ Image
          </button>

          <button
            onClick={addLink}
            className={`px-3 py-1 rounded ${
              editor.isActive('link') ? 'bg-blue-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            🔗 Link
          </button>

          {/* Quote */}
          <button
            onClick={() => editor.chain().focus().toggleBlockquote().run()}
            className={`px-3 py-1 rounded ${
              editor.isActive('blockquote') ? 'bg-gray-200' : 'hover:bg-gray-100'
            }`}
            type="button"
          >
            💬 Quote
          </button>
        </div>
      </div>

      {/* Editor Content */}
      <div className="mb-6">
        <EditorContent 
          editor={editor} 
          className="min-h-[400px] focus-within:ring-2 focus-within:ring-blue-500 focus-within:ring-opacity-50 rounded-lg"
        />
      </div>

      {/* Status and Stats */}
      <div className="flex justify-between items-center text-sm text-gray-600 mb-6">
        <div className="flex gap-4">
          <span>{wordCount} words</span>
          <span>{characterCount} characters</span>
          {characterCount > 9000 && (
            <span className="text-red-600">
              Character limit approaching ({10000 - characterCount} remaining)
            </span>
          )}
        </div>
        <div>
          {articleId && (
            <span className="text-gray-500">
              {articleId ? 'Editing existing article' : 'New article'}
            </span>
          )}
        </div>
      </div>

      {/* Action Buttons */}
      <div className="flex gap-4 justify-end">
        <button
          onClick={handleSaveDraft}
          disabled={isSaving || isLoading}
          className="px-6 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isSaving ? 'Saving...' : 'Save Draft'}
        </button>
        
        <button
          onClick={handlePublish}
          disabled={isSaving || isLoading}
          className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isSaving ? 'Publishing...' : 'Publish'}
        </button>
      </div>
    </div>
  );
};

export default ArticleEditor;
